import logo from './logo.svg';
import field from './field.png';
import ReactDOM from "react-dom";
import React ,{ useRef, useEffect }  from 'react';
import CanvasDraw from "react-canvas-draw";

import './App.css';

function App() {

  const [team, setTeam] = React.useState('Greece');

  return (
    <div className="App">
      <h1>This is the SuperLeague's Football App </h1>

      <Select_Team parameter={setTeam}/>
      <Field />

    </div>
  );
}

const Canvas = props => {

  const canvasRef = props.pass_par;

  useEffect(() => {
    const canvasRef = props.pass_par;

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    //Our first draw
    context.fillStyle = '#000000'
    context.fillRect(0, 0, context.canvas.width, context.canvas.height)
  }, [])

  return <canvas ref={canvasRef} {...props}/>
}

function Filter(props){

  //alert("ok");

  //var ok=document.getElementById("field");
  //alert(ok);


  return (
    <div>
    <p>Canvas:</p>
    <canvas id="myCanvas" width="240" height="297" >

    </canvas>
    </div>


  );

}


function Field(){

  const inputRef = useRef(null);
  //var ok=document.getElementById("field");
  //alert(ok);
  return (

    <div >
      <img src="./field.png" ref={inputRef}  alt="football_field" class="football_field" id="field"></img>

      <Filter pass_par={inputRef}/>
      <Canvas pass_par={inputRef}/>
    </div>
  );


}
function Select_Team(props){

  const handleEvent = event => {
    //alert(typeof(props.parameter));
    props.parameter(event.target.value);
  };

  return (
      <div>
      <label for="teams">Choose your favorite team : </label>

      <select name="teams" id="teams" onChange={handleEvent}>
        <option value="aek">Aek</option>
        <option value="panathinaikos">Panathinaikos</option>
        <option value="aris">Aris</option>
        <option value="paok">Paok</option>
      </select>
      </div>
  );

}


export default App;
