function Show(props){

  const team_to_show = props.parameter_team ;
  const all_teams=props.teams;

  console.log(team_to_show);
  const searched_team = all_teams.filter(each =>
    each.team.toLowerCase().includes(team_to_show.toLowerCase())
    );

  alert(searched_team.team)

  return (

    <div >
      Coach : {searched_team.team}
